mapper.select:
select u.user_id, u.user_name, u.phone, mph.id, mph.amount, mph.redeemed_points, mph.change_type, mph.change_desc, mph.before_points, mph.after_points, mph.create_date, mph.remark from member_points_history mph left join user_info u on mph.user_id = u.user_id
u.user_name LIKE CONCAT('%', #{memberPoints.userName}, '%')
u.phone LIKE CONCAT('%', #{memberPoints.phone}, '%')
mph.create_date >= #{memberPoints.startTime}
mph.create_date <= #{memberPoints.endTime}
mph.change_type = #{memberPoints.changeType}
order by mph.update_date desc
